ExportDifferential.Statement:
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
WITH targetCoupon AS ( SELECT order_id FROM w2_Order WHERE w2_Order.date_changed >= @date_changed )
SELECT @@ EXPORT_FIELDS @@ FROM w2_OrderCoupon INNER JOIN targetCoupon ON ( w2_OrderCoupon.order_id = targetCoupon.order_id ) ORDER BY w2_OrderCoupon.order_id ASC
