ExportDifferential.Statement:
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
WITH targetItem AS ( SELECT order_id FROM w2_Order WHERE w2_Order.date_changed >= @date_changed )
SELECT @@ EXPORT_FIELDS @@ FROM w2_OrderItem INNER JOIN targetItem ON ( w2_OrderItem.order_id = targetItem.order_id ) ORDER BY w2_OrderItem.order_id ASC
