ExportDifferential.Statement:
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
WITH targetOwner AS ( SELECT order_id FROM w2_Order WHERE w2_Order.date_changed >= @date_changed )
SELECT @@ EXPORT_FIELDS @@ FROM w2_OrderOwner INNER JOIN targetOwner ON ( w2_OrderOwner.order_id = targetOwner.order_id ) ORDER BY w2_OrderOwner.order_id ASC
