ExportDifferential.Statement:
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
WITH targetPriceByTaxRate AS ( SELECT order_id FROM w2_Order WHERE w2_Order.date_changed >= @date_changed )
SELECT @@ EXPORT_FIELDS @@ FROM w2_OrderPriceByTaxRate INNER JOIN targetPriceByTaxRate ON ( w2_OrderPriceByTaxRate.order_id = targetPriceByTaxRate.order_id ) ORDER BY w2_OrderPriceByTaxRate.order_id, w2_OrderPriceByTaxRate.key_tax_rate ASC
