ExportDifferential.Statement:
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
WITH targetOrder AS ( SELECT order_id FROM w2_Order WHERE w2_Order.date_changed >= @date_changed ), targetOwner AS ( SELECT order_id FROM w2_OrderOwner WHERE w2_OrderOwner.date_changed >= @date_changed ), targetItem AS ( SELECT DISTINCT order_id FROM w2_OrderItem WHERE w2_OrderItem.date_changed >= @date_changed ), targetShipping AS ( SELECT DISTINCT order_id FROM w2_OrderShipping WHERE w2_OrderShipping.date_changed >= @date_changed ), targetCoupon AS ( SELECT order_id FROM w2_OrderCoupon WHERE w2_OrderCoupon.date_changed >= @date_changed ), targetOrderIds AS ( SELECT DISTINCT order_id FROM ( SELECT * FROM targetOrder UNION ALL SELECT * FROM targetOwner UNION ALL SELECT * FROM targetItem UNION ALL SELECT * FROM targetShipping UNION ALL SELECT * FROM targetCoupon ) AS tt )
SELECT @@ EXPORT_FIELDS @@ FROM w2_OrderSetPromotion INNER JOIN targetOrderIds ON ( w2_OrderSetPromotion.order_id = targetOrderIds.order_id ) ORDER BY w2_OrderSetPromotion.order_id ASC
