InsertMallExhibitsConfig.Statement:


DECLARE  @MallExhibitsCount int

 SELECT  @MallExhibitsCount = COUNT(product_id)
   FROM  w2_MallExhibitsConfig
  WHERE  w2_MallExhibitsConfig.shop_id = @shop_id 
    AND  w2_MallExhibitsConfig.product_id = @product_id

 IF @MallExhibitsCount = 0
   -- 登録
   INSERT INTO 
           w2_MallExhibitsConfig
           (
             shop_id,
             product_id,
             exhibits_flg1,
             exhibits_flg2,
             exhibits_flg3,
             exhibits_flg4,
             exhibits_flg5,
             exhibits_flg6,
             exhibits_flg7,
             exhibits_flg8,
             exhibits_flg9,
             exhibits_flg10,
             exhibits_flg11,
             exhibits_flg12,
             exhibits_flg13,
             exhibits_flg14,
             exhibits_flg15,
             exhibits_flg16,
             exhibits_flg17,
             exhibits_flg18,
             exhibits_flg19,
             exhibits_flg20,
             date_created,
             date_changed,
             last_changed
           )
   VALUES  (
             @shop_id,
             @product_id,
             @exhibits_flg1,
             @exhibits_flg2,
             @exhibits_flg3,
             @exhibits_flg4,
             @exhibits_flg5,
             @exhibits_flg6,
             @exhibits_flg7,
             @exhibits_flg8,
             @exhibits_flg9,
             @exhibits_flg10,
             @exhibits_flg11,
             @exhibits_flg12,
             @exhibits_flg13,
             @exhibits_flg14,
             @exhibits_flg15,
             @exhibits_flg16,
             @exhibits_flg17,
             @exhibits_flg18,
             @exhibits_flg19,
             @exhibits_flg20,
             getdate(),
             getdate(),
             @last_changed
           )
