USERMEMBERRANKHIS_SEARCH_WHERE.Statement:

WHERE  (
         (
           @user_id_like_escaped = '' 
           OR 
           w2_UserMemberRankHistory.user_id LIKE @user_id_like_escaped + '%' ESCAPE '#'
         )      -- ユーザーID
         AND
         (
           @before_rank_id = '' 
           OR 
           w2_UserMemberRankHistory.before_rank_id = @before_rank_id
         )    -- 更新前ランクID
         AND
         (
           @after_rank_id = '' 
           OR 
           w2_UserMemberRankHistory.after_rank_id = @after_rank_id
         )    -- 更新後ランクID
         AND
         (
           @date_created_from IS NULL 
           OR 
           w2_UserMemberRankHistory.date_created >= @date_created_from
         )            
         AND 
         (
           @date_created_to IS NULL 
           OR 
           w2_UserMemberRankHistory.date_created < @date_created_to
         )
       )
