InsertProductStockHistoryForOrder.Statement:

INSERT  w2_ProductStockHistory
        (
          order_id,
          shop_id,
          product_id,
          variation_id,
          action_status,
          add_stock,
          add_realstock,
          add_realstock_reserved,
          update_stock,
          update_realstock,
          update_realstock_reserved,
          last_changed
        )
  VALUES
        (
          @order_id,
          @shop_id,
          @product_id,
          @variation_id,
          @action_status,
          @add_stock,
          @add_realstock,
          @add_realstock_reserved,
          @update_stock,
          @update_realstock,
          @update_realstock_reserved,
          @last_changed
        )
